import { useEffect, useState } from 'react';
import useInfiniteScroll from '../hooks/useInfiniteScroll';
import Loading from './Loading';
import '../assets/index.css';
import CloseIssueIcon from './CloseIssueIcon';
function IssueList({status}) {
  const [isFetching, setIsFetching] = useInfiniteScroll(fetchMoreListItems);
  const[data,setData]=useState()
var num=1
  function fetchMoreListItems() {
    fetch(`http://localhost:9000/issues?page=&issuesFilter=${status}`).then(res=>res.json())
    .then(res=>setData(res))
  }
  useEffect(()=>{
    fetch(`http://localhost:9000/issues?page=1&issuesFilter=${status}`).then(res=>res.json())
    .then(res=>setData(res))
  },[status])
// console.log(isFetching)
  return (
    <>
    {data===undefined?<Loading />:
    data.map(data=><div className="issues" data-testid="issues">{data.title}</div>)}
      {/* <div className="issues" data-testid="issues"></div> */}
      {/* <Loading /> */}

      <button onClick={fetchMoreListItems}>add</button>
    </>
  );
}

export default IssueList;
